import React from 'react';
import { getFields } from './model/mock.js';
import { Database } from './model/database.js';
import { Select } from './model/select.js';
import { Utils } from './model/utils.js';
import { Task } from './model/task.js';
import './index.css';

export class TaskForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            compName: "Form"
        }
        this.clearForm = this.clearForm.bind(this);
    }

   /* changeForm(){

    }*/

    submitForm(){
        console.log(this.props);
        //this.props.submitForm();
    }

    clearForm(){
        console.log("called here");
        this.props.clearForm();
    }

    render(){
        var fieldList = getFields();
        var fieldGroups = fieldList.map((fields) => {
        var ipt = <input id={fields.name} className="form-control"
            type={fields.type} onChange={this.onFormChange}
            value={this.state[fields.propName]}
            placeholder={fields.placeholder} max={fields.max} min={fields.min} maxLength={fields.maxlength}></input>;
        var x = <div key={fields.name} className="form-group">
            <label>{fields.name}</label>
            {ipt}
          </div>;
          return (x);
        });

        return (<div>{fieldGroups}
          <button className="btn btn-primary" onClick={this.submitForm}>Submit</button>
          <button className="btn btn-primary" onClick={this.clearForm}>Clear</button>
        </div>);
    }
}

export default TaskForm;
