import React from 'react';
import { getFields } from './model/mock.js';
import './index.css';
import { Task } from './model/task.js';

export class TaskForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            compName: "Form",
            objForm: new Task(),
            clear: false
        }
        this.clearForm = this.clearForm.bind(this);
        this.submitForm = this.submitForm.bind(this);
        this.blurField = this.blurField.bind(this);
    }

    blurField(fn) {
        console.log(fn.target);
    }

    submitForm() {
        console.log("called here");
        this.props.submitForm();
    }

    clearForm() {
        console.log("called here");
        this.props.clearForm();
    }

    createFields() {
        var fieldList = getFields();
        return fieldList.map((fields) => {
            var input = <input id={fields.name} className="form-control"
                type={fields.type}
                onBlur={fn => this.blurField(fn)}
                value={this.state[fields.propName]}
                placeholder={fields.placeholder}
                max={fields.max} min={fields.min} maxLength={fields.maxlength}></input>;
            var fieldGroup = <div key={fields.name} className="form-group">
                <label>{fields.name}</label>
                {input}
            </div>;
            return (fieldGroup);
        });
    }

    setSelectedTask() {
        var selectedTask = this.props.objSelected;
        if (selectedTask.id_task > 0
          && selectedTask.id_task !== this.state.objForm.id_task) {
          this.setState({ selectedObj: selectedTask });
          this.setState({
            desc: selectedTask.description,
            prio: selectedTask.priority,
            date: selectedTask.date,
            status: selectedTask.status,
            userId: selectedTask.id_user,
            taskId: selectedTask.id_task
          });
        }

        if (this.state.clear) {
          var u = new Utils();
          this.setState({ clear: false });
          this.setState({
            desc: "",
            prio: 1,
            date: u.getDate(),
            status: 0,
            userId: 1,
            taskId: 0,
          });
        }
      }

    render() {
        this.setSelectedTask();
        var fieldGroups = this.createFields();
        return (<div>{fieldGroups}
            <button className="btn btn-primary" onClick={this.submitForm}>Submit</button>
            <button className="btn btn-primary" onClick={this.clearForm}>Clear</button>
        </div>);
    }
}

export default TaskForm;
