import './index.css';
import React from 'react';
import { getFields } from './model/mock.js';
import { Task } from './model/task.js';
import { Utils } from './model/utils.js';

export class TaskForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            compName: "Form",
            objForm: new Task(),
            clear: false
        }
        this.clearForm = this.clearForm.bind(this);
        this.submitForm = this.submitForm.bind(this);
        this.blurField = this.blurField.bind(this);
    }

    blurField(e) {
        var updObj = this.state.objForm;
        if (e.target.id === "Description") {
            updObj.description = e.target.value;
        } else if (e.target.id === "Priority") {
            updObj.priority = e.target.value;
        } else if (e.target.id === "Due Date") {
            updObj.date = e.target.value;
        }
        this.setState({
            objForm: updObj
        });
    }

    submitForm() {
        var ctx = this;
        this.clearForm();
        ctx.props.submitForm(ctx.state.objForm);
    }

    clearForm(callback) {
        this.setState({
            clear: true
        }, function () {
            this.props.clearForm();
        });
    }

    createFields() {
        var fieldList = getFields();
        return fieldList.map((fields) => {
            var fieldGroup = <div key={fields.name} className="form-group">
                <label>{fields.name}</label>
                <input id={fields.name} className="form-control"
                type={fields.type}
                onBlur={fn => this.blurField(fn)}
                defaultValue={this.state.objForm[fields.propName]}
                placeholder={fields.placeholder}
                max={fields.max} min={fields.min} maxLength={fields.maxlength}></input>
            </div>;
            return (fieldGroup);
        });
    }

    setSelectedTask(callback) {
        var updObj;
        var selectedTask = this.props.objSelected;

        if (this.state.clear) {
            console.log("Today's date on setSelectedTask");
            var u = new Utils();
            updObj = new Task("", 1, u.getDate(), 0, 0);
            this.setState({
                clear: false,
                objForm: updObj
            });
        } else {
            if (selectedTask && selectedTask.id_task > 0
                && selectedTask.id_task !== this.state.objForm.id_task) {
                updObj = new Task(selectedTask.description,
                    selectedTask.priority,
                    selectedTask.id_user,
                    selectedTask.date,
                    selectedTask.id_task,
                    selectedTask.status);
                this.setState({
                    selectedObj: selectedTask,
                    objForm: updObj,
                }, );
            }
        }
    }

    dateChange(e){
        var objForm = this.state.objForm;
        var updObj = new Task(objForm.description,
            objForm.priority,
            objForm.id_user,
            e.target.value,
            objForm.id_task,
            objForm.status);
        this.setState({
            objForm: updObj,
        }, );
    }

    render() {
        this.setSelectedTask();
        return (<div>
            <div className="form-group">
                <label>Description</label>
                <input id="Description" className="form-control"
                    type="text" onBlur={fn => this.blurField(fn)}
                    defaultValue={this.state.objForm.description}
                ></input>

                <label>Priority</label>
                <input id="Priority" className="form-control"
                    type="number" onBlur={fn => this.blurField(fn)}
                    defaultValue={this.state.objForm.priority}
                ></input>

                <label>Due Date</label>
                <input id="Due Date" className="form-control"
                    type="date" onBlur={fn => this.blurField(fn)}
                    onChange={fn => this.dateChange(fn)}
                    value={this.state.objForm.date}
                ></input>
            </div>
            <button className="btn btn-primary" onClick={this.submitForm}>Submit</button>
            <button className="btn btn-primary" onClick={this.clearForm}>Clear</button>
        </div>);
    }
}

export default TaskForm;
