import React from 'react';
import './index.css';
import { Database } from './model/database';
import { Utils } from './model/utils';

export class StatsViewer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            compName: "Stats",
            listTab1: []
        }
    }

    fetchTable() {
        var context = this;
        var list = [];
        var u = new Utils();
        var db = new Database();
        var filter = { "startingDate": u.getDate()};
        var r = db.sendRequest("GraphController", "getPriorityIndexGraph", filter);
        r.then(function (response) {
            for (var json of response.data) {
                list.push(json);
            }
            context.setState({
                listTab1: list
            });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    componentDidMount() {
        this.fetchTable();
    }

    render() {
        console.log(this.state.listTab1);
        if (this.state.listTab1) {
            return this.state.listTab1.map((val) => {
                return <div key={val.id_task}>{val.index}</div>
            });
        } else {
            return <div>Stats</div>
        }
    }
}

export default StatsViewer;
