import React from 'react';
import { getFields } from './model/mock.js';
import { Database } from './model/database.js';
import { Select } from './model/select.js';
import { Utils } from './model/utils.js';
import { Task } from './model/task.js';
import { TaskList } from './TaskList.js';
import { TaskForm } from './TaskForm.js';
import './index.css';

export class TaskFrame extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            compName: "Main",
            objList: null,
            objSelected: null,
        }
        this.getTasks = this.getTasks.bind(this);
    }

    getTasks(callback) {
        var db = new Database();
        var ctx = this;
        var params = new Select("status = 0", 10, "dueDate desc");
        db.select("Task", params).then(function (response) {
            ctx.setState({ objList: {response} });
            if (callback) {
                callback();
            }
        });
    }

    setSelected() {

    }

    completeTask() {

    }

    removeTask() {

    }

    submitForm(fn) {
        console.log("submitForm");
    }

    changeForm(fn) {
        console.log("changeForm");
    }

    clearForm(fn) {
        console.log("clearForm");
    }

    componentDidMount() {
        this.getTasks();
    }

    render() {
        if (this.state.objList) {
            return <div>
                <TaskForm
                    submitForm={fn => this.submitForm(fn)}
                    clearForm={fn => this.clearForm(fn)}
                    changeForm={fn => this.changeForm(fn)}
                >
                </TaskForm>
                <TaskList
                    objList={this.state.objList.response.data}
                ></TaskList>
            </div>
        } else {
            return <div>
            <TaskForm
                submitForm={fn => this.submitForm(fn)}
                clearForm={fn => this.clearForm(fn)}
                changeForm={fn => this.changeForm(fn)}
            >
            </TaskForm>
        </div>
        }
    }
}

export default TaskFrame;
